import React from 'react';
import styles from "../../styles/login.module.css";
import { } from "@material-ui/icons";
import { useRef } from 'react';
import {loginCall} from "../../apiCalls"
import { useHistory } from 'react-router';
import { useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';

export default function Login() {

    const email = useRef();
    const password = useRef();
    const history = useHistory()
    
    const {isFetching, error, dispatch} = useContext(AuthContext)

    const handleClick = (e) => {
        e.preventDefault();
        loginCall(
            { email: email.current.value, password: password.current.value },
            dispatch).then(error => {
                if (error)
                email.current.setCustomValidity("User Login Failed");
            })
    }

    const handleRegisterRedirect = (e) => {
        history.push('/register')
      }

    return (
        <div>
            <div className={styles.login}>
                <div className={styles.loginWrapper}>
                    <div className={styles.loginLeft}>
                        <h3 className={styles.loginLogo}>NIET SOCIAL!!</h3>
                        <span className={styles.loginDesc}>
                            Come join your fellow NIET'ians and connect with them.
                        </span>
                    </div>
                    <div className={styles.loginRight}>
                        <form className={styles.loginBox} onSubmit={handleClick}>
                            <input 
                                placeholder="Email" 
                                type= "email" 
                                required 
                                className={styles.loginInput} 
                                ref={email}
                            />

                            <input 
                                placeholder="Password" 
                                type= "password" 
                                required 
                                minLength="6"
                                className={styles.loginInput} 
                                ref={password} 
                            />
                            <button className={styles.loginButton} type="submit" disabled={isFetching}>
                                {isFetching ? "loading" : "Log in"}
                            </button>
                            <span className={styles.loginForgot}>
                                Forgot Password?
                            </span>
                            <button className={styles.loginRegisterButton} onClick={handleRegisterRedirect}>
                                Create a New Account
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}
